package client

import (
	"encoding/base64"
	"testing"
)

func TestFromKubeConfig(t *testing.T) {
	kubeconfigBase64 := ""
	kubeConfig, err := base64.StdEncoding.DecodeString(kubeconfigBase64)
	if err != nil {
		t.Fatal(err)
	}
	_, _, err = FromKubeConfig(kubeConfig)
	if err != nil {
		t.Fatal(err)
	}
}
